import React, { Fragment, useEffect ,useState} from "react";
import { useParams, Link } from "react-router-dom";

const ProductDetails = ({ setAuth }) => {
  // Component logic here
  const { id } = useParams();
  const [product, setProduct] = useState([]);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log("Fetching product:", id);
        const response = await fetch(`http://localhost:3006/api/productDetails/${id}`);
        const data = await response.json();
        setProduct(data);
        //console.log("the desired Response:", response);
        console.log("the desired Data:", data);
        console.log("the desired product:", product);
      } catch (error) {
        console.error("Error fetching product:", error);
      }
    };

    fetchProduct();
  }, [id]);

  return (
    <Fragment>
        <div className="product-details-container">
            <h1>ProductDetails</h1>
            {product ? (
            <div className="product-item" key={product[0].productid}>
                <h2>{product.name}</h2>
                <ul>
                <li>Price: {product[0].price}</li>
                <li>Quantity in Stock: {product.quantityinstock}</li>
                <li>Category ID: {product.categoryid}</li>
                </ul>
            </div>
            ) : (
            <p>Loading...</p>
            )}
        </div>
    </Fragment>
  )
};

export default ProductDetails;
