import React,{Fragment,useState,useEffect} from "react";
import './LoginPage.css'; // Import CSS file

import CategoryList from "../components/CategoryList";

const Home = ({setAuth}) => {
   
  const [FirstName, setFirstName] = useState("");
  async function getFirstName() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token }
      });

      const parseRes = await response.json();
      setFirstName(parseRes.user_firstname);
    } catch (err) {
      console.error(err.message);
    }
  }
  useEffect(() => {
    getFirstName();
  }, []);

  return (
    <Fragment>
    <div className="container">
        <h1 className="font-weight-light display text-center">Home{FirstName}</h1>
        <button onClick={()=>setAuth(false)} className="btn btn-primary">LogOut</button>
       {/*  <CategoryList />*/}

    </div>
    </Fragment>
  );
};

export default Home;
