import React, { Fragment, useState, useEffect } from "react";
import './LoginPage.css'; // Import CSS file

import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInfo";
import './Home.css'; // Import CSS file

const Home = ({ setAuth }) => {

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");

  async function getUserInfo() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token }
      });
      const userData = await response.json();
      setFirstName(userData.firstname);
      setLastName(userData.lastname);
      setEmail(userData.email);
      setCity(userData.city);
      setDistrict(userData.district);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const logout = async (e) => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      setAuth(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <div className="container">
        <h1 className="home-heading text-center">Home</h1>
        <button onClick={e => logout(e)} className="btn btn-primary">LogOut</button>
        <UserInformation
          firstName={firstName}
          lastName={lastName}
          email={email}
          city={city}
          district={district}
        /> {/* Render UserInformation component */}
        <CategoryList /> {/* Render the CategoryList component */}
      </div>
    </Fragment>
  );
};

export default Home;
