import React, { useState, useEffect } from "react";
import "./Home.css";
import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInfo";

const Home = ({ setAuth }) => {

  const [isLoggedIn, setIsLoggedIn] = useState(true); // State to track login status
  const [userInfo, setUserInfo] = useState({}); // State to store user information

  useEffect(() => {
    const getUserInfo = async () => {
      try {
        const response = await fetch("http://localhost:3006/dashboard/", {
          method: "GET",
          headers: { token: localStorage.token },
        });

        const userData = await response.json();
        console.log(userData);

        // Update user information if logged in
        if (response.ok) {
          setIsLoggedIn(true);
          setUserInfo(userData);
        }
      } catch (err) {
        console.error(err.message);
      }
    };

    getUserInfo();
  }, []);

  const handleLogout = () => {
    try {
      localStorage.removeItem("token");
      setAuth
      setIsLoggedIn(false); // Update login status
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <div>
      <div className="container">
        <h1
          className="home-heading text-center"
          style={{ fontSize: "3rem", color: "cyan" }}
        >
          Welcome to Home
        </h1>

        {isLoggedIn && ( // Render user information if logged in
          <>
            <h2 className="text-center" style={{ color: "black" }}>
              {userInfo.firstName} {userInfo.lastName}
            </h2>
            <UserInformation
              userInfo={userInfo}
              handleLogout={handleLogout}
            />
          </>
        )}
      </div>
      <CategoryList />
    </div>
  );
};

export default Home;
