import React, { Fragment, useState, useEffect } from "react";
import "./Home.css"; // Import CSS file for Home component

import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInformation";

const Home = ({ setAuth }) => {
  const [userInfo, setUserInfo] = useState({
    firstName: "",
    lastName: "",
    email: "",
    city: "",
    district: ""
    // Add more attributes as needed
  });

  async function getUserInfo() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token }
      });

      const userData = await response.json();
      console.log(userData);
      setUserInfo(userData);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const logout = async (e) => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      setAuth(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <div className="home-container">
        <header className="header">
          <h1 className="header-title">Welcome, {userInfo.firstName}</h1>
          <button onClick={logout} className="logout-btn">Logout</button>
        </header>
        <main>
          <UserInformation userInfo={userInfo} />
          <CategoryList />
        </main>
      </div>
    </Fragment>
  );
};

export default Home;
