import React, { Fragment, useState, useEffect } from "react";
import './LoginPage.css'; // Import CSS file

import CategoryList from "../components/CategoryList";

const Home = ({ setAuth }) => {

  const [userInfo, setUserInfo] = useState({
    firstName: "",
    lastName: "",
    email: "",
    city: "",
    district: ""
    // Add more attributes as needed
  });

  async function getUserInfo() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token }
      });

      const userData = await response.json();
      console.log(userData);
      setUserInfo(userData);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const logout = async (e) => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      setAuth(false);
    } catch (err) {
      console.error(err.message);
    }
  };

  return (
    <Fragment>
      <div className="container">
        <br></br>
        <br></br>

        <h1 className="font-weight-light display text-center">Home {userInfo.firstName}</h1>
        <button onClick={e => logout(e)} className="btn btn-primary">LogOut</button>
        <br></br>
        <div>
          <p><strong>First Name:</strong> {userInfo.firstName}</p>
          <p><strong>Last Name:</strong> {userInfo.lastName}</p>
          <p><strong>Email:</strong> {userInfo.email}</p>
          <p><strong>City:</strong> {userInfo.city}</p>
          <p><strong>District:</strong> {userInfo.district}</p>
          {/* Add more user info attributes here */}
        </div>
        <CategoryList /> {/* Render the CategoryList component */}
      </div>
    </Fragment>
  );
};

export default Home;
