import React, { Fragment, useState, useEffect } from "react";
import "./Home.css";
import CategoryList from "../components/CategoryList";
import UserInformation from "../routes/userInfo";

const Home = ({ setAuth }) => {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [city, setCity] = useState("");
  const [district, setDistrict] = useState("");

  async function getUserInfo() {
    try {
      const response = await fetch("http://localhost:3006/dashboard/", {
        method: "GET",
        headers: { token: localStorage.token },
      });

      const userData = await response.json();
      console.log(userData);
      setFirstName(userData.firstname);
      setLastName(userData.lastname);
      setEmail(userData.email);
      setCity(userData.city);
      setDistrict(userData.district);
    } catch (err) {
      console.error(err.message);
    }
  }

  useEffect(() => {
    getUserInfo();
  }, []);

  const logout = async (e) => {
    e.preventDefault();
    try {
      localStorage.removeItem("token");
      setAuth(false);
    } catch (err) {
      console.error(err.message);
    }
  };

 return (
  <div className="category-list-container">
    <ul className="list-group">
      {categories.map((category, index) => (
        <li
          key={index}
          className={
            index % 2 === 0
              ? "list-group-item bg-secondary"
              : "list-group-item bg-dark text-white"
          }
        >
          <div className="button-container">
            <div className="category-info">
              <span className="category-name-extended">
                {category.categoryname}
              </span>
            </div>
            <div className="btn-container">
              <button
                className="btn btn-outline-warning"
                onClick={() => navigateToProducts(category)}
              >
                Go
              </button>
            </div>
          </div>
        </li>
      ))}
    </ul>
  </div>
);
};

export default Home;
